import React, { useCallback } from "react";
import styled from "styled-components";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import EmailSignup from "./EmailSignUp";
import logo from "./assets/logo.svg"; // Import the logo.svg

const App = () => {
  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine); // Correctly load the engine
  }, []);

  return (
    <AppContainer>
      {/* Add the Header */}
      <Header>
        <Logo>
          <img src={logo} alt="Logo" />
        </Logo>
        <Heading>a Personalised health companion coming your way!</Heading>
        <Nav>
          <a href="#bioloop">BioLoop &gt;</a>
          <a href="#team">Team &gt;</a>
          <a href="#about">About &gt;</a>
        </Nav>
      </Header>

      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: {
              value: "#000", // Base color is black
            },
            image:
              "linear-gradient(to bottom, rgba(0, 0, 0, 0.9), rgba(75, 0, 130, 0.9) 72%, rgba(238, 130, 238, 0.8) 95%)",
            size: "cover",
          },
          particles: {
            color: { value: "#fff" },
            move: { enable: true, speed: 0.5 },
            number: { value: 150 },
            opacity: { value: 0.8 }, // Adjust transparency
            size: { value: { min: 1, max: 3 } },
          },
        }}
      />

      {/* Main Content */}
      <Content>
        <h1>Be our early customer &gt;</h1>
        <p>Please enter your email below to stay updated</p>
        <InputContainer>
          <EmailSignup />
        </InputContainer>
      </Content>
    </AppContainer>
  );
};

export default App;

// Styled Components
const AppContainer = styled.div`
  position: relative;
  height: 100vh;
  overflow: hidden;
  display: flex;
  flex-direction: column; /* Adjusted for header and content */
  justify-content: flex-start; /* Align items to start */
  background: linear-gradient(to bottom, #0f1e35, #35144f, #5a2b68, #873d84, #a65299);
`;

/* Header Section */
const Header = styled.header`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding: 20px;
  color: white;
  text-align: center;
  z-index: 2;
`;

const Logo = styled.div`
  img {
    width: 40px;
    height: 40px;
  }
`;

const Heading = styled.h2`
  margin: 10px 0;
  font-size: 1.2rem;
  font-weight: 300;
`;

const Nav = styled.nav`
  display: flex;
  gap: 20px;
  margin-top: 10px;

  a {
    color: white;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s ease;

    &:hover {
      color: #ff79c6;
    }
  }
`;

/* Content Section */
const Content = styled.div`
  position: relative;
  text-align: center;
  color: white;
  z-index: 1;
  margin-top: 50px; /* Added margin to push it below the header */

  h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    font-weight: bold;
    animation: fadeIn 2s ease-in-out;
  }

  p {
    font-size: 1.2rem;
    margin-bottom: 2rem;
    color: #ddd;
  }
`;

const InputContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
`;
