import React, { useCallback } from "react";
import styled from "styled-components";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import EmailSignup from "./EmailSignUp"; // Assuming EmailSignup is in the same folder
import logo from './assets/logo.svg';

const App = () => {
  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine);
  }, []);

  return (
    <AppContainer>
      <NavBar>
        <Logo>
          {/* <img src="path-to-logo.png" alt="Logo" /> */}
          <img src={logo} alt="Logo" />


        </Logo>
        <Tagline>a Personalised health companion coming your way!</Tagline>
        <NavLinks>
          <a href="#bioloop">BioLoop &gt;</a>
          <a href="#team">Team &gt;</a>
          <a href="#about">About &gt;</a>
        </NavLinks>
      </NavBar>
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: { value: "#000" },
            image: "linear-gradient(to bottom, rgba(0,0,0,0.9), rgba(75,0,130,0.9) 72%, rgba(238,130,238,0.8) 95%)",
            size: "cover",
          },
          particles: {
            color: { value: "#fff" },
            move: { enable: true, speed: 0.5 },
            number: { value: 150 },
            opacity: { value: 0.8 },
            size: { value: { min: 1, max: 3 } },
          },
        }}
      />
      <Content>
        <EmailSignup />
      </Content>
    </AppContainer>
  );
};

export default App;

// Styled Components
const AppContainer = styled.div`
  position: relative;
  height: 100vh;
  overflow: hidden;
`;

const NavBar = styled.div`
  position: absolute;
  top: 0;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 50px;
  color: white;
  z-index: 10;
`;

const Logo = styled.div`
  img {
    width: 30px; /* Adjust size to fit your design */
    height: 30px;
  }
`;

const Tagline = styled.h1`
  font-size: 1rem;
  font-weight: normal;
  color: #ddd;
  text-align: center;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 20px;

  a {
    color: #ddd;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s;

    &:hover {
      color: #ff79c6;
    }
  }
`;

const Content = styled.div`
  position: relative;
  text-align: center;
  margin-top: 100px; /* Adjust to place below the tagline */
`;
