import React, { useCallback } from 'react';
import styled from 'styled-components';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';

const App = () => {
  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine); // Correctly load the engine
  }, []);

  return (
    <AppContainer>
      {/* <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: { value: 'transparent' }, // Transparent to show gradient
          },
          particles: {
            color: { value: '#ffffff' }, // White particles
            move: { enable: true, speed: 0.5 },
            number: { value: 150 }, // Increase the number for more stars
            opacity: { value: 0.8 },
            size: { value: { min: 1, max: 2 } },
            shape: { type: 'circle' },
          },
        }}
      /> */}

<Particles
  id="tsparticles"
  init={particlesInit}
  options={{
    background: {
      color: {
        value: "#000", // Base color is black
      },
      image: "linear-gradient(to bottom, rgba(0, 0, 0, 0.9), rgba(75, 0, 130, 0.9) 72%, rgba(238, 130, 238, 0.8) 95%)",
      size: "cover",
    },
    particles: {
      color: { value: "#fff" },
      move: { enable: true, speed: 0.5 },
      number: { value: 150 },
      opacity: { value: 0.2 }, // Adjust transparency
      size: { value: { min: 1, max: 3 } },
    },
  }}
/>

      <Content>
        <h1>Be our early customer &gt;</h1>
        <p>Please enter your email below to stay updated</p>
        <InputContainer>
          <input type="email" placeholder="Enter your email" />
          <button>Submit</button>
        </InputContainer>
      </Content>
    </AppContainer>
  );
};

export default App;

// Styled Components
const AppContainer = styled.div`
  position: relative;
  height: 100vh;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(to bottom, #0f1e35, #35144f, #5a2b68, #873d84, #a65299); /* Updated gradient */
`;

const Content = styled.div`
  position: relative;
  text-align: center;
  color: white;
  z-index: 1;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    font-weight: bold;
    animation: fadeIn 2s ease-in-out;
  }

  p {
    font-size: 1.2rem;
    margin-bottom: 2rem;
    color: #ddd;
  }
`;

const InputContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;

  input {
    padding: 10px 20px;
    border: 2px solid #fff;
    border-radius: 20px;
    background: transparent;
    color: white;
    outline: none;

    &::placeholder {
      color: #ddd;
    }
  }

  button {
    padding: 10px 20px;
    border: none;
    border-radius: 20px;
    background: #ff79c6;
    color: white;
    font-weight: bold;
    cursor: pointer;
    transition: background-color 0.3s ease;

    &:hover {
      background: #ff007a;
    }
  }
`;
