import React, { useCallback } from 'react';
import styled from 'styled-components';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import EmailSignup from "./EmailSignUp"; // Corrected import statement

const App = () => {
  const particlesInit = useCallback(async (engine) => {
    await loadFull(engine); // Correctly load the engine
  }, []);

  return (
    <AppContainer>
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          background: {
            color: {
              value: "#000", // Base color is black
            },
            image: "linear-gradient(to bottom, rgba(0, 0, 0, 0.9), rgba(75, 0, 130, 0.9) 72%, rgba(238, 130, 238, 0.8) 95%)",
            size: "cover",
          },
          particles: {
            color: { value: "#fff" },
            move: { enable: true, speed: 0.5 },
            number: { value: 150 },
            opacity: { value: 0.8 }, // Adjust transparency
            size: { value: { min: 1, max: 3 } },
          },
        }}
      />
      <Content>
        <h1>Be our early customer &gt;</h1>
        <p>Please enter your email below to stay updated</p>
        <InputContainer>
          <EmailSignup /> {/* Use the EmailSignup component here */}
        </InputContainer>
      </Content>
    </AppContainer>
  );
};

export default App;

// Styled Components
const AppContainer = styled.div`
  position: relative;
  height: 100vh;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(to bottom, #0f1e35, #35144f, #5a2b68, #873d84, #a65299); /* Updated gradient */
`;

const Content = styled.div`
  position: relative;
  text-align: center;
  color: white;
  z-index: 1;

  h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    font-weight: bold;
    animation: fadeIn 2s ease-in-out;
  }

  p {
    font-size: 1.2rem;
    margin-bottom: 2rem;
    color: #ddd;
  }
`;

const InputContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
`;
